SELECT CUSTOMER.C_NATIONKEY, REGION.R_COMMENT, NATION.N_NAME, AVG(CUSTOMER.C_NATIONKEY), MAX(REGION.R_NAME) FROM CUSTOMER, NATION, REGION GROUP BY CUSTOMER.C_NATIONKEY, REGION.R_COMMENT, NATION.N_NAME HAVING AVG(CUSTOMER.C_NATIONKEY) < 0 AND MAX(REGION.R_NAME) > 'CUSTOMER.C_CUSTKEY' ORDER BY AVG(CUSTOMER.C_NATIONKEY) DESC;
