SELECT CUSTOMER.C_CUSTKEY, SUPPLIER.S_ACCTBAL, NATION.N_COMMENT, SUM(CUSTOMER.C_CUSTKEY) FROM CUSTOMER, NATION, SUPPLIER WHERE SUPPLIER.S_SUPPKEY < 7 GROUP BY CUSTOMER.C_CUSTKEY, SUPPLIER.S_ACCTBAL, NATION.N_COMMENT HAVING SUM(CUSTOMER.C_CUSTKEY) > 93 ORDER BY SUPPLIER.S_ACCTBAL DESC, CUSTOMER.C_CUSTKEY DESC;
