SELECT LINEITEM.L_RETURNFLAG, PART.P_SIZE, PARTSUPP.PS_COMMENT, MAX(LINEITEM.L_TAX) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY LINEITEM.L_RETURNFLAG, PART.P_SIZE, PARTSUPP.PS_COMMENT ORDER BY MAX(LINEITEM.L_TAX) DESC;
