SELECT PARTSUPP.PS_SUPPLYCOST, LINEITEM.L_SHIPDATE, CUSTOMER.C_ACCTBAL, ORDERS.O_TOTALPRICE, COUNT(ORDERS.O_ORDERDATE) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY WHERE ORDERS.O_CUSTKEY < 11749 GROUP BY PARTSUPP.PS_SUPPLYCOST, LINEITEM.L_SHIPDATE, CUSTOMER.C_ACCTBAL, ORDERS.O_TOTALPRICE HAVING COUNT(ORDERS.O_ORDERDATE) < 5 ORDER BY COUNT(ORDERS.O_ORDERDATE) DESC;
