SELECT LINEITEM.L_ORDERKEY, ORDERS.O_TOTALPRICE, CUSTOMER.C_ADDRESS, REGION.R_NAME, NATION.N_COMMENT FROM REGION, NATION, CUSTOMER, ORDERS, LINEITEM WHERE LINEITEM.L_LINESTATUS <= 'F' AND CUSTOMER.C_ACCTBAL > 1007.18 ORDER BY ORDERS.O_TOTALPRICE ASC, NATION.N_COMMENT DESC, LINEITEM.L_ORDERKEY ASC, REGION.R_NAME ASC, CUSTOMER.C_ADDRESS DESC;
