SELECT ORDERS.O_ORDERKEY, CUSTOMER.C_ACCTBAL, REGION.R_NAME, NATION.N_NAME, COUNT(REGION.R_NAME) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY GROUP BY ORDERS.O_ORDERKEY, CUSTOMER.C_ACCTBAL, REGION.R_NAME, NATION.N_NAME;
