SELECT LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_SUPPKEY, PART.P_COMMENT, MAX(PART.P_SIZE) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE LINEITEM.L_QUANTITY >= 34 GROUP BY LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_SUPPKEY, PART.P_COMMENT;
