SELECT LINEITEM.L_ORDERKEY, CUSTOMER.C_MKTSEGMENT, NATION.N_NATIONKEY, PARTSUPP.PS_AVAILQTY, SUPPLIER.S_SUPPKEY, ORDERS.O_TOTALPRICE FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY ORDER BY NATION.N_NATIONKEY DESC;
