SELECT LINEITEM.L_TAX, CUSTOMER.C_NATIONKEY, ORDERS.O_TOTALPRICE, PARTSUPP.PS_SUPPLYCOST FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY ORDER BY CUSTOMER.C_NATIONKEY ASC, LINEITEM.L_TAX DESC, PARTSUPP.PS_SUPPLYCOST ASC, ORDERS.O_TOTALPRICE ASC;
