SELECT NATION.N_NATIONKEY, CUSTOMER.C_MKTSEGMENT, COUNT(CUSTOMER.C_PHONE), MAX(NATION.N_COMMENT), MIN(CUSTOMER.C_NATIONKEY) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY WHERE NATION.N_NATIONKEY != 9427 AND CUSTOMER.C_ACCTBAL > -78.56 GROUP BY NATION.N_NATIONKEY, CUSTOMER.C_MKTSEGMENT;
