SELECT LINEITEM.L_SUPPKEY, NATION.N_COMMENT, SUPPLIER.S_NAME, PARTSUPP.PS_AVAILQTY FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY ORDER BY NATION.N_COMMENT ASC, LINEITEM.L_SUPPKEY ASC;
