SELECT CUSTOMER.C_ACCTBAL, NATION.N_NATIONKEY, REGION.R_COMMENT, COUNT(CUSTOMER.C_NAME), MAX(NATION.N_REGIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY GROUP BY CUSTOMER.C_ACCTBAL, NATION.N_NATIONKEY, REGION.R_COMMENT ORDER BY CUSTOMER.C_ACCTBAL ASC, REGION.R_COMMENT DESC;
