SELECT REGION.R_COMMENT, SUPPLIER.S_SUPPKEY, NATION.N_NAME, COUNT(SUPPLIER.S_SUPPKEY), MIN(SUPPLIER.S_PHONE) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY GROUP BY REGION.R_COMMENT, SUPPLIER.S_SUPPKEY, NATION.N_NAME HAVING MIN(SUPPLIER.S_PHONE) > '24-696-997-4969' ORDER BY MIN(SUPPLIER.S_PHONE) ASC;
