SELECT ORDERS.O_TOTALPRICE, LINEITEM.L_SHIPDATE, CUSTOMER.C_ADDRESS, PARTSUPP.PS_PARTKEY, PART.P_MFGR FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY ORDER BY ORDERS.O_TOTALPRICE ASC, PARTSUPP.PS_PARTKEY DESC, LINEITEM.L_SHIPDATE ASC, PART.P_MFGR ASC, CUSTOMER.C_ADDRESS DESC;
