SELECT PARTSUPP.PS_SUPPLYCOST, ORDERS.O_ORDERSTATUS, LINEITEM.L_SHIPINSTRUCT, PART.P_TYPE FROM PART, PARTSUPP, LINEITEM, ORDERS WHERE PART.P_PARTKEY >= 200 AND ORDERS.O_TOTALPRICE > 61651.51 ORDER BY PART.P_TYPE DESC, LINEITEM.L_SHIPINSTRUCT ASC, ORDERS.O_ORDERSTATUS DESC, PARTSUPP.PS_SUPPLYCOST ASC;
