SELECT LINEITEM.L_LINENUMBER, PARTSUPP.PS_COMMENT, ORDERS.O_SHIPPRIORITY, CUSTOMER.C_NATIONKEY, MAX(CUSTOMER.C_ACCTBAL) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY GROUP BY LINEITEM.L_LINENUMBER, PARTSUPP.PS_COMMENT, ORDERS.O_SHIPPRIORITY, CUSTOMER.C_NATIONKEY;
