SELECT LINEITEM.L_SHIPDATE, ORDERS.O_ORDERSTATUS, NATION.N_COMMENT, CUSTOMER.C_PHONE FROM NATION JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY ORDER BY NATION.N_COMMENT DESC, CUSTOMER.C_PHONE ASC;
