SELECT PART.P_PARTKEY, ORDERS.O_SHIPPRIORITY, CUSTOMER.C_ADDRESS, NATION.N_REGIONKEY, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_PARTKEY FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY ORDER BY SUPPLIER.S_ACCTBAL ASC, CUSTOMER.C_ADDRESS ASC, PART.P_PARTKEY ASC;
