SELECT REGION.R_REGIONKEY, NATION.N_NAME, SUPPLIER.S_NAME, AVG(REGION.R_REGIONKEY), COUNT(SUPPLIER.S_ACCTBAL), MAX(NATION.N_REGIONKEY), COUNT(SUPPLIER.S_NATIONKEY) FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY REGION.R_REGIONKEY, NATION.N_NAME, SUPPLIER.S_NAME ORDER BY MAX(NATION.N_REGIONKEY) ASC, AVG(REGION.R_REGIONKEY) DESC, COUNT(SUPPLIER.S_NATIONKEY) DESC, COUNT(SUPPLIER.S_ACCTBAL) ASC;
