SELECT PARTSUPP.PS_AVAILQTY, SUPPLIER.S_NATIONKEY FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY WHERE SUPPLIER.S_ACCTBAL < 5755.94 AND SUPPLIER.S_COMMENT >= 'S UNWIND SILENTLY FURIOUSLY REGULAR COURTS. FINAL REQUESTS ARE DEPOSITS. REQUESTS WAKE QUIETLY BLIT' AND SUPPLIER.S_SUPPKEY <= 8;
