SELECT LINEITEM.L_COMMITDATE, PARTSUPP.PS_SUPPKEY, COUNT(LINEITEM.L_EXTENDEDPRICE) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY GROUP BY LINEITEM.L_COMMITDATE, PARTSUPP.PS_SUPPKEY HAVING COUNT(LINEITEM.L_EXTENDEDPRICE) <= 1 ORDER BY LINEITEM.L_COMMITDATE ASC, PARTSUPP.PS_SUPPKEY DESC;
