SELECT SUPPLIER.S_ACCTBAL, LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST, MIN(SUPPLIER.S_ADDRESS) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE LINEITEM.L_SUPPKEY <= 629 GROUP BY SUPPLIER.S_ACCTBAL, LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST HAVING MIN(SUPPLIER.S_ADDRESS) != 'GCDM2RJRZL5QLTVZC' ORDER BY PARTSUPP.PS_SUPPLYCOST ASC, LINEITEM.L_SHIPMODE ASC, SUPPLIER.S_ACCTBAL ASC;
