SELECT PARTSUPP.PS_AVAILQTY, SUPPLIER.S_SUPPKEY, MAX(PARTSUPP.PS_COMMENT) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY PARTSUPP.PS_AVAILQTY, SUPPLIER.S_SUPPKEY HAVING MAX(PARTSUPP.PS_COMMENT) >= 'PRESS THEODOLITES SLEEP CAREFULLY ABOUT THE BLITHELY UNUSUAL REQUESTS. QUICKLY FINAL DEPOSITS BREACH SLYLY ' ORDER BY PARTSUPP.PS_AVAILQTY ASC;
