SELECT SUPPLIER.S_NAME, NATION.N_NATIONKEY, REGION.R_REGIONKEY FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE REGION.R_REGIONKEY !=  QUICKLY PENDIN AND SUPPLIER.S_ADDRESS != 'GCDM2RJRZL5QLTVZC' AND REGION.R_COMMENT > 'ONS MIGHT WAKE QUICKLY ACCORDING TO TH';
