SELECT ORDERS.O_SHIPPRIORITY, CUSTOMER.C_ADDRESS, SUPPLIER.S_NAME, NATION.N_NAME FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE ORDERS.O_ORDERKEY < 259 ORDER BY ORDERS.O_SHIPPRIORITY DESC, NATION.N_NAME DESC;
