SELECT ORDERS.O_SHIPPRIORITY, CUSTOMER.C_COMMENT, REGION.R_REGIONKEY, NATION.N_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY ORDER BY ORDERS.O_SHIPPRIORITY DESC, REGION.R_REGIONKEY ASC, NATION.N_REGIONKEY DESC;
