SELECT ORDERS.O_CLERK, NATION.N_COMMENT, CUSTOMER.C_NAME, SUPPLIER.S_NATIONKEY FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY ORDER BY SUPPLIER.S_NATIONKEY DESC, ORDERS.O_CLERK ASC;
