SELECT CUSTOMER.C_ADDRESS, REGION.R_COMMENT, NATION.N_REGIONKEY, MAX(REGION.R_REGIONKEY) FROM CUSTOMER, NATION, REGION WHERE CUSTOMER.C_PHONE >= '19-190-993-9281' GROUP BY CUSTOMER.C_ADDRESS, REGION.R_COMMENT, NATION.N_REGIONKEY HAVING MAX(REGION.R_REGIONKEY) > ORDERS ORDER BY CUSTOMER.C_ADDRESS ASC;
