SELECT LINEITEM.L_RETURNFLAG, PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERSTATUS, MAX(ORDERS.O_ORDERPRIORITY) FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY GROUP BY LINEITEM.L_RETURNFLAG, PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERSTATUS;
