SELECT LINEITEM.L_COMMENT, ORDERS.O_CUSTKEY, CUSTOMER.C_ACCTBAL, NATION.N_REGIONKEY, REGION.R_COMMENT, COUNT(LINEITEM.L_EXTENDEDPRICE) FROM REGION, NATION, CUSTOMER, ORDERS, LINEITEM GROUP BY LINEITEM.L_COMMENT, ORDERS.O_CUSTKEY, CUSTOMER.C_ACCTBAL, NATION.N_REGIONKEY, REGION.R_COMMENT HAVING COUNT(LINEITEM.L_EXTENDEDPRICE) = 6;
