SELECT SUPPLIER.S_ACCTBAL, NATION.N_COMMENT, AVG(SUPPLIER.S_NATIONKEY), COUNT(NATION.N_NAME) FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE NATION.N_NAME >= 'D FOXES. FINAL, EVEN BRAIDS SLEEP SLYLY SLYLY REGULAR IDEAS. UNUSUAL IDEAS ABOVE' AND SUPPLIER.S_SUPPKEY < 1 AND NATION.N_COMMENT < '1993-05-01' GROUP BY SUPPLIER.S_ACCTBAL, NATION.N_COMMENT ORDER BY NATION.N_COMMENT ASC;
