SELECT SUPPLIER.S_COMMENT, LINEITEM.L_LINENUMBER, PARTSUPP.PS_AVAILQTY, SUM(LINEITEM.L_DISCOUNT), SUM(SUPPLIER.S_NATIONKEY) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE LINEITEM.L_LINESTATUS = 'O' GROUP BY SUPPLIER.S_COMMENT, LINEITEM.L_LINENUMBER, PARTSUPP.PS_AVAILQTY;
