SELECT ORDERS.O_ORDERPRIORITY, CUSTOMER.C_ADDRESS, NATION.N_COMMENT, REGION.R_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY WHERE ORDERS.O_ORDERPRIORITY = '2-HIGH         ';
