SELECT CUSTOMER.C_PHONE, LINEITEM.L_SHIPMODE, ORDERS.O_COMMENT, NATION.N_REGIONKEY FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY WHERE LINEITEM.L_SHIPDATE >= '1995-09-28' ORDER BY CUSTOMER.C_PHONE ASC;
