SELECT PARTSUPP.PS_SUPPKEY, LINEITEM.L_DISCOUNT, ORDERS.O_ORDERSTATUS, CUSTOMER.C_COMMENT, NATION.N_COMMENT, SUPPLIER.S_PHONE, PART.P_BRAND FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY ORDER BY PARTSUPP.PS_SUPPKEY DESC, LINEITEM.L_DISCOUNT ASC, NATION.N_COMMENT ASC, ORDERS.O_ORDERSTATUS ASC, PART.P_BRAND DESC, SUPPLIER.S_PHONE DESC;
