SELECT LINEITEM.L_LINESTATUS, ORDERS.O_ORDERSTATUS, COUNT(ORDERS.O_ORDERKEY), COUNT(LINEITEM.L_LINESTATUS) FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY LINEITEM.L_LINESTATUS, ORDERS.O_ORDERSTATUS ORDER BY COUNT(ORDERS.O_ORDERKEY) ASC, COUNT(LINEITEM.L_LINESTATUS) DESC;
