SELECT NATION.N_NATIONKEY, SUPPLIER.S_PHONE, PARTSUPP.PS_SUPPKEY, PART.P_SIZE, REGION.R_COMMENT, MIN(PART.P_RETAILPRICE) FROM PART, PARTSUPP, SUPPLIER, NATION, REGION WHERE PART.P_SIZE <= 21 AND NATION.N_NAME = '1997-01-18' GROUP BY NATION.N_NATIONKEY, SUPPLIER.S_PHONE, PARTSUPP.PS_SUPPKEY, PART.P_SIZE, REGION.R_COMMENT;
