SELECT CUSTOMER.C_NAME, ORDERS.O_ORDERDATE, LINEITEM.L_QUANTITY FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE ORDERS.O_ORDERDATE < '1995-01-25' AND ORDERS.O_COMMENT <= ' REGULAR IDEAS NAG THINLY FURIOUSLY S' ORDER BY CUSTOMER.C_NAME DESC, ORDERS.O_ORDERDATE DESC, LINEITEM.L_QUANTITY ASC;
