SELECT SUPPLIER.S_ACCTBAL, CUSTOMER.C_CUSTKEY, NATION.N_NAME, PART.P_BRAND, PARTSUPP.PS_COMMENT FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY;
