SELECT CUSTOMER.C_CUSTKEY, NATION.N_COMMENT, MIN(CUSTOMER.C_NAME), MIN(NATION.N_NATIONKEY), MAX(CUSTOMER.C_MKTSEGMENT) FROM CUSTOMER, NATION WHERE NATION.N_COMMENT != 'USLY REGULAR EXCUSES. SILENT, EVEN SHEAVES ARE ACCORDING TO THE REGULAR REQUESTS. PACKAGES GROW BLITHELY SLYLY REGULAR ACCOUNTS. CA' GROUP BY CUSTOMER.C_CUSTKEY, NATION.N_COMMENT HAVING MIN(NATION.N_NATIONKEY) > CUSTOMER.C_CUSTKEY AND MIN(CUSTOMER.C_NAME) != 'CUSTOMER#000000057' AND MAX(CUSTOMER.C_MKTSEGMENT) < 'HOUSEHOLD ';
