SELECT ORDERS.O_TOTALPRICE, CUSTOMER.C_PHONE, NATION.N_NAME, REGION.R_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY ORDER BY NATION.N_NAME ASC, ORDERS.O_TOTALPRICE DESC;
