SELECT ORDERS.O_CLERK, LINEITEM.L_ORDERKEY, CUSTOMER.C_NATIONKEY, NATION.N_NATIONKEY, SUPPLIER.S_NAME, COUNT(CUSTOMER.C_COMMENT) FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE LINEITEM.L_SHIPDATE <= '1997-03-19' GROUP BY ORDERS.O_CLERK, LINEITEM.L_ORDERKEY, CUSTOMER.C_NATIONKEY, NATION.N_NATIONKEY, SUPPLIER.S_NAME;
