SELECT LINEITEM.L_DISCOUNT, PARTSUPP.PS_AVAILQTY, PART.P_PARTKEY, ORDERS.O_CLERK FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY ORDER BY PARTSUPP.PS_AVAILQTY DESC, PART.P_PARTKEY DESC, LINEITEM.L_DISCOUNT DESC;
