SELECT CUSTOMER.C_NAME, ORDERS.O_SHIPPRIORITY, NATION.N_REGIONKEY, REGION.R_COMMENT FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY ORDER BY CUSTOMER.C_NAME ASC, REGION.R_COMMENT DESC, ORDERS.O_SHIPPRIORITY ASC;
