SELECT PART.P_COMMENT, SUPPLIER.S_NAME, PARTSUPP.PS_COMMENT, NATION.N_NAME FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY ORDER BY SUPPLIER.S_NAME ASC, PARTSUPP.PS_COMMENT DESC;
