SELECT CUSTOMER.C_NATIONKEY, NATION.N_REGIONKEY, REGION.R_REGIONKEY, COUNT(NATION.N_REGIONKEY) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY CUSTOMER.C_NATIONKEY, NATION.N_REGIONKEY, REGION.R_REGIONKEY ORDER BY REGION.R_REGIONKEY DESC, NATION.N_REGIONKEY DESC;
