SELECT REGION.R_NAME, PARTSUPP.PS_PARTKEY, CUSTOMER.C_ACCTBAL, SUPPLIER.S_NATIONKEY, NATION.N_NAME, ORDERS.O_COMMENT, LINEITEM.L_COMMENT FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY WHERE LINEITEM.L_SHIPDATE != '1995-01-02';
