SELECT NATION.N_REGIONKEY, SUPPLIER.S_COMMENT, CUSTOMER.C_MKTSEGMENT, PARTSUPP.PS_AVAILQTY FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY WHERE SUPPLIER.S_ADDRESS = ' N KD4ON9OM IPW3,GF0JBOQDD7TGRZRDDZ' AND PARTSUPP.PS_AVAILQTY <= 2656 ORDER BY NATION.N_REGIONKEY DESC, CUSTOMER.C_MKTSEGMENT ASC, SUPPLIER.S_COMMENT ASC, PARTSUPP.PS_AVAILQTY DESC;
