SELECT PARTSUPP.PS_AVAILQTY, PART.P_RETAILPRICE, COUNT(PARTSUPP.PS_COMMENT), SUM(PARTSUPP.PS_AVAILQTY) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY GROUP BY PARTSUPP.PS_AVAILQTY, PART.P_RETAILPRICE HAVING SUM(PARTSUPP.PS_AVAILQTY) < 4482 ORDER BY PART.P_RETAILPRICE ASC, PARTSUPP.PS_AVAILQTY ASC;
