SELECT CUSTOMER.C_NAME, NATION.N_REGIONKEY, REGION.R_REGIONKEY, MIN(REGION.R_NAME) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY GROUP BY CUSTOMER.C_NAME, NATION.N_REGIONKEY, REGION.R_REGIONKEY ORDER BY MIN(REGION.R_NAME) DESC;
